import React, { useState, useRef } from 'react';
import { useHistory } from 'react-router-dom';
import { Header } from '@buffetjs/custom';
import { Padded } from '@buffetjs/core';
import { Formik } from 'formik';
import { useIntl } from 'react-intl';
import { request, useGlobalContext } from 'strapi-helper-plugin';
import BaselineAlignement from '../../../components/BaselineAlignement';
import ContainerFluid from '../../../components/ContainerFluid';
import FormCard from '../../../components/FormBloc';
import SizedInput from '../../../components/SizedInput';
import getTrad from '../../../utils/getTrad';
import pluginId from '../../../pluginId';
import UsersPermissions from '../../../components/UsersPermissions';
import { usePlugins } from '../../../hooks';
import schema from './utils/schema';

const CreatePage = () => {
  const { formatMessage } = useIntl();
  const { emitEvent } = useGlobalContext();
  const { goBack } = useHistory();
  const [isSubmiting, setIsSubmiting] = useState(false);
  const { permissions, routes, policies, isLoading } = usePlugins();
  const permissionsRef = useRef();

  const headerActions = (handleSubmit, handleReset) => {
    if (isLoading) {
      return [];
    }

    return [
      {
        label: formatMessage({
          id: getTrad('app.components.Button.reset'),
          defaultMessage: 'Reset',
        }),
        onClick: () => {
          handleReset();
          permissionsRef.current.resetForm();
        },
        color: 'cancel',
        type: 'button',
      },
      {
        label: formatMessage({
          id: getTrad('app.components.Button.save'),
          defaultMessage: 'Save',
        }),
        onClick: handleSubmit,
        color: 'success',
        type: 'submit',
        isLoading: isSubmiting,
      },
    ];
  };

  const handleCreateRoleSubmit = data => {
    strapi.lockAppWithOverlay();
    setIsSubmiting(true);

    const permissions = permissionsRef.current.getPermissions();

    Promise.resolve(
      request(`/${pluginId}/roles`, {
        method: 'POST',
        body: { ...data, ...permissions, users: [] },
      })
    )
      .then(() => {
        emitEvent('didCreateRole');
        strapi.notification.toggle({
          type: 'success',
          message: { id: 'Settings.roles.created' },
        });
        // Forcing redirecting since we don't have the id in the response
        // TODO
        goBack();
      })
      .catch(err => {
        console.error(err);
        strapi.notification.toggle({
          type: 'warning',
          message: { id: 'notification.error' },
        });
      })
      .finally(() => {
        setIsSubmiting(false);
        strapi.unlockApp();
      });
  };

  return (
    <Formik
      initialValues={{ name: '', description: '' }}
      onSubmit={handleCreateRoleSubmit}
      validationSchema={schema}
    >
      {({ handleSubmit, values, errors, handleReset, handleChange, handleBlur, touched }) => {
        return (
          <form onSubmit={handleSubmit}>
            <ContainerFluid padding="0">
              <Header
                title={{
                  label: formatMessage({
                    id: getTrad('Settings.roles.create.title'),
                    defaultMessage: 'Create a role',
                  }),
                }}
                content={formatMessage({
                  id: getTrad('Settings.roles.create.description'),
                  defaultMessage: 'Define the rights given to the role',
                })}
                actions={headerActions(handleSubmit, handleReset)}
                isLoading={isLoading}
              />
              <BaselineAlignement top size="3px" />
              <FormCard
                isLoading={isLoading}
                title={formatMessage({
                  id: getTrad('EditPage.form.roles'),
                  defaultMessage: 'Role details',
                })}
              >
                <SizedInput
                  label="Settings.roles.form.input.name"
                  defaultMessage="Name"
                  name="name"
                  type="text"
                  error={errors.name && touched.name ? { id: errors.name } : null}
                  onBlur={handleBlur}
                  value={values.name}
                  onChange={handleChange}
                />
                <SizedInput
                  label="Settings.roles.form.input.description"
                  defaultMessage="Description"
                  name="description"
                  type="textarea"
                  error={
                    errors.description && touched.description ? { id: errors.description } : null
                  }
                  onBlur={handleBlur}
                  value={values.description}
                  onChange={handleChange}
                  // Override the default height of the textarea
                  style={{ height: 115 }}
                />
              </FormCard>
            </ContainerFluid>
            <div style={{ paddingTop: '1.8rem' }} />
            {!isLoading && (
              <UsersPermissions
                ref={permissionsRef}
                permissions={permissions}
                routes={routes}
                policies={policies}
              />
            )}
            <Padded top size="md" />
          </form>
        );
      }}
    </Formik>
  );
};

export default CreatePage;
